package com.womai.shop.domain.front;import java.io.Serializable;public class Favorite extends com.womai.shop.domain.common.Favorite implements Serializable {	private static final long serialVersionUID = 1L;	private Product product;	public void clear() {		super.clear();		if(product!=null){			product.clear();		}	}	public Product getProduct() {		return product;	}	public void setProduct(Product product) {		this.product = product;	}	}