package com.womai.shop.manage.admin.impl;import java.util.List;import com.womai.shop.manage.ServersManager;import com.womai.shop.manage.admin.OrderdetailService;import com.womai.shop.domain.admin.Orderdetail;import com.womai.shop.dao.admin.OrderdetailDao;import com.womai.shop.domain.admin.ReportInfo;public class OrderdetailServiceImpl extends ServersManager<Orderdetail>		implements OrderdetailService {	private OrderdetailDao orderdetailDao;	public void setOrderdetailDao(OrderdetailDao orderdetailDao) {		this.orderdetailDao = orderdetailDao;	}	@Override	public List<ReportInfo> reportProductSales(Orderdetail orderdetail) {		return orderdetailDao.reportProductSales(orderdetail);	}}