package com.womai.shop.web.action.manage.email;import com.womai.shop.web.action.BaseAction;import com.womai.shop.manage.admin.EmailService;import com.womai.shop.domain.admin.Email;public class EmailAction extends BaseAction<Email> {	private static final long serialVersionUID = 1L;	private EmailService emailService;	public EmailService getEmailService() {		return emailService;	}	protected void selectListAfter() {		pager.setPagerUrl("email!selectList.action");	}	public void setEmailService(EmailService emailService) {		this.emailService = emailService;	}	public Email getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Email();		}	}	public void insertAfter(Email e) {		e.clear();	}}