package com.womai.shop.web.action.manage.express;import com.womai.shop.web.action.BaseAction;import com.womai.shop.manage.admin.ExpressService;import com.womai.shop.domain.admin.Express;public class ExpressAction extends BaseAction<Express> {	private static final long serialVersionUID = 1L;	private ExpressService expressService;	public ExpressService getExpressService() {		return expressService;	}	protected void selectListAfter() {		pager.setPagerUrl("express!selectList.action");	}	public void setExpressService(ExpressService expressService) {		this.expressService = expressService;	}	public Express getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Express();		}	}	public void insertAfter(Express e) {		e.clear();	}}